import React, {useEffect, useState} from 'react'
import "./Post.css"
import {Link} from "react-router-dom";
import { createClient } from 'contentful';

export default function Post() {

  const [blogPosts, setBlogPosts] = useState([])
  const [sidebarBio, setSidebarBio] = useState(null);

  const client = createClient({space: "c2coqf0hrs8z", accessToken: "rO_zDMrrcg28Vx21OpMLpsSkTxmX7uOL8molgDLYoPs"})

  useEffect(() => {
    const getAllEntries = async () => {
      try{
        const blogEntries = await client.getEntries({
          content_type: 'blogPost',
        })
        setBlogPosts(blogEntries.items);

        const bioEntry = await client.getEntries({
          content_type: 'sidebarBio',
          limit: 1,
        });
        if(bioEntry.items.length > 0){
          setSidebarBio(bioEntry.items[0]);
        }
      } catch(err) {
        console.log("Error with fetching blog entries: ", err)
      }
    }
    getAllEntries()
  }, [])

  return (
    <>
      {blogPosts?.map((post) => (
        <Link
          to={`/postDetails/${post.sys.id}`}
          className="postLink"
          key={post.sys.id}
        >
          <div className="post">
            <img className="postImg"
              src={post.fields.blogImage.fields.file.url}
              alt={post.fields.blogTitle}
            />
            <div className="postInfo">
              <div className="postCats">
                  <span className="postCat">Teaching</span>
                  <span className="postCat">Coaching</span>
              </div>
              <span className="postTitle">{post.fields.blogTitle}</span>
              <hr />
              <span className="postDate">{post.fields.createdDate}</span>
            </div>
            <p className="postDesc">
              {post.fields.postContent}
            </p>
          </div>
        </Link>
      ))}

      {sidebarBio && (
        <div className="sidebarBio">
          <img src={sidebarBio.fields?.sidebarImage.fields.file.url}/>
          <p>{sidebarBio.fields?.sidebarText}</p>
        </div>
      )}

    </>
  )
}
